package mypackage;

public class Type {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Arithematics Operators");
		int a = 5, b = 2;
		System.out.println("a+b:"+(a+b));
		System.out.println("a-b:"+(a-b));
		System.out.println("a*b:"+(a*b));
		System.out.println("a/b:"+(a/b));
		System.out.println("a%b:"+(a%b));
			
	}

}
