package mypackage;

public class Type02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        System.out.println("Unary Operators");
        int a = -5;
        int b = 10;
        
        System.out.println(-(a));
        System.out.println(++b);
        System.out.println(b++);
        System.out.println(b);
	}

}
